/*
  Here we are creating a table having very rought geometries in order to make a distinction between East as West US Coast, which we will use later on our analysis.
*/

CREATE TABLE "context_data"."us_region"();
ALTER TABLE "context_data"."us_region" ADD COLUMN "id" SERIAL CONSTRAINT "pk_us_region" PRIMARY KEY;
ALTER TABLE "context_data"."us_region" ADD COLUMN "region" CHAR(4);

SELECT AddGeometryColumn('context_data','us_region','geom',4326,'MULTIPOLYGON',2);

INSERT INTO "context_data"."us_region" ("region", "geom") VALUES ('West', '');
INSERT INTO "context_data"."us_region" ("region", "geom") VALUES ('East', '');
CREATE INDEX "us_region_geom_geom_idx" ON "context_data"."us_region" USING GIST ("geom");


